import React, { useContext } from "react";
import { BsMoonFill, BsFillSunFill, BsSunFill } from "react-icons/bs";
import ThemeContext from "../context/ThemeContext";

const Header = () => {
  const { ligthMode, modeChanger } = useContext(ThemeContext);
  console.log(ligthMode);
  return (
    <header>
      <div className="logo">devfinder</div>
      <div className="themeMode" onClick={modeChanger}>
        <i>{ligthMode ? <BsMoonFill /> : <BsSunFill />}</i>
        {ligthMode ? "Dark" : "Light"}
      </div>
    </header>
  );
};

export default Header;
