import React, { useState, useEffect, useContext } from "react";
import "./index.css";
import SearchBar from "./components/SearchBar";
import UserCard from "./components/UserCard";
import Header from "./components/Header";
import ThemeContext from "./context/ThemeContext";

const App = () => {
  const [query, setQuery] = useState("");
  const [data, setData] = useState("");
  const [err, setErr] = useState(false);
  // const [lightMode, setLightMode] = useState("light");
  const { lightMode } = useContext(ThemeContext);
  useEffect(() => {
    if (query !== "") {
      getGithubUserData();
    }
  }, [query]);
  useEffect(() => {
    const light = getComputedStyle(document.documentElement).getPropertyValue(
      ":root"
    );
    console.log(light);
  }, [lightMode]);

  async function getGithubUserData() {
    const resp = await fetch(` https://api.github.com/users/${query} `);
    if (!resp.ok) {
      return setErr(true);
    }
    setErr(false);
    const data = await resp.json();
    setData(data);
  }

  return (
    <div className="app" data-theme={lightMode ? "light" : "dark"}>
      <Header />
      <SearchBar setQuery={setQuery} err={err} />
      <UserCard data={data} />
    </div>
  );
};

export default App;
