import React, { useState } from "react";

function UserPlan() {
  // which plan is selected?
  const [selectedPlan, setSelectedPlan] = useState("basic");
  //   montly or yearly pricing
  const [pricingOption, setPricingOption] = useState("monthly");

  const handlePlanSelect = (plan) => {
    setSelectedPlan(plan);
  };

  const handlePricingOptionSelect = (option) => {
    setPricingOption(option);
  };

  return (
    <div>
      <h2>Select a Plan:</h2>
      <div>
        <PlanOption
          title="Basic Plan"
          price={pricingOption === "monthly" ? "$9.99/mo" : "$99.99/yr"}
          selected={selectedPlan === "basic"}
          onSelect={() => handlePlanSelect("basic")}
        />
        <PlanOption
          title="Standard Plan"
          price={pricingOption === "monthly" ? "$19.99/mo" : "$199.99/yr"}
          selected={selectedPlan === "standard"}
          onSelect={() => handlePlanSelect("standard")}
        />
        <PlanOption
          title="Premium Plan"
          price={pricingOption === "monthly" ? "$29.99/mo" : "$299.99/yr"}
          selected={selectedPlan === "premium"}
          onSelect={() => handlePlanSelect("premium")}
        />
      </div>
      <h2>Select a Pricing Option:</h2>
      <div>
        <PricingOptionButton
          label="Monthly"
          isSelected={pricingOption === "monthly"}
          onSelect={() => handlePricingOptionSelect("monthly")}
        />
        <PricingOptionButton
          label="Yearly"
          isSelected={pricingOption === "yearly"}
          onSelect={() => handlePricingOptionSelect("yearly")}
        />
      </div>
    </div>
  );
}

function PlanOption({ title, price, selected, onSelect }) {
  return (
    <div>
      <h3>{title}</h3>
      <p>{price}</p>
      <button onClick={onSelect}>{selected ? "Selected" : "Select"}</button>
    </div>
  );
}

function PricingOptionButton({ label, isSelected, onSelect }) {
  return (
    <button onClick={onSelect}>
      {isSelected ? `${label} (Selected)` : label}
    </button>
  );
}
export default UserPlan;
