import { configureStore } from "@reduxjs/toolkit";
import { planReducer, changePlan } from "./slices/planSlice";
import {
  userInfoReducer,
  changeEmail,
  changeName,
  changePhone,
} from "./slices/userInfoSlice";

const store = configureStore({
  userInfo: userInfoReducer,
  plan: planReducer,
});
export { store, changeEmail, changeName, changePhone, changePlan };
