import { configureStore } from "@reduxjs/toolkit";
import { changePlan, changeTenure, planReducer } from "./slices/userPlanSlice";
import {
  userInfoReducer,
  changeEmail,
  changeName,
  changePhone,
} from "./slices/userInfoSlice";

const store = configureStore({
  reducer: {
    userInfo: userInfoReducer,
  },
});
export { store, changeEmail, changeName, changePhone };
